from itertools import accumulate

# 시간 초과
class Solution:
    def answerQueries(nums, queries):
        answer, list_q = [] , []
        nums = sorted(nums)
        a = 0

        while len(queries) >= 1:
            list_q.append(nums[a])
            a += 1

            if sum(list_q) == queries[0]:
                queries.pop(0)
                list_q.clear()
                answer.append(a)
                a = 0
                continue

            if sum(list_q) > queries[0] or queries[0] < nums[a-1]:
                queries.pop(0)
                list_q.clear()
                answer.append(a-1)
                a = 0
                continue


            if len(nums) == a:
                queries.pop(0)
                list_q.clear()
                answer.append(a)
                a = 0
                continue
                
        return answer

# Try... 2 Clear
# Runtime 306ms Memory 14.2Mb
class Solution2:
    def answerQueries(nums, queries):
        c = []
        sum = 0
        answer = []
        nums = sorted(nums)

        for num in nums:
            sum += num
            c.append(sum)
        

        for query in queries:
            result = 0
            for i, v in enumerate(c):
                if v <= query:
                    result = i + 1
                else:
                    break
            answer.append(result)
        return answer

# Best Solved
# 이분 탐색 풀이
class Solution:
    def answerQueries(self, A: List[int], queries: List[int]) -> List[int]:
        A = list(accumulate(sorted(A)))
        return [bisect_right(A, q) for q in queries]

            


